// import CRibbon from "./Ribbon";

export default {
  name: "CHeader",
  components: {
    // CRibbon,
    CLogo,
    CButton,
  },
  props: {
    active: Boolean,
    navPosition: {
      type: String,
      default: "",
    },
    hideNav: {
      type: Boolean,
      default: false,
    },
    hideSignin: {
      type: Boolean,
      default: false,
    },
    bottomDivider: {
      type: Boolean,
      default: false,
    },
  },
  data() {
    return {
      isActive: true,
    };
  },
  watch: {
    active(value) {
      this.isActive = value;
    },
  },
  methods: {
    menuToggle() {
      this.isActive = !this.isActive;
    },
  },
  mounted() {
    if (window.innerWidth >= 960) {
      this.isActive = false;
    } else {
      this.isActive = true;
    }

    document.addEventListener("keydown", this.keyPress);
    document.addEventListener("click", this.clickOutside);
  },
  beforeDestroy() {
    document.addEventListener("keydown", this.keyPress);
    document.removeEventListener("click", this.clickOutside);
  },
};
</script>

